class Solution:
    def countVowels(self, word: str) -> int:
        """ The main aim of the problem is to count the substrings in which a vowel must present """
        count = 0
        l = len(word)
        for pos in range(l):
            if word[pos] in "aeiou":
                count += (l - pos) * (pos + 1)
        return count

